import React from 'react';
import TreeView from '@material-ui/lab/TreeView';
import TreeItem from '@material-ui/lab/TreeItem';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import {
  Box,
  ListItemIcon,
  ListItemText,
  Menu,
  MenuItem,
} from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import { observer } from 'mobx-react';

@observer
class ExList extends React.Component {
  state = {
    anchorEl: null,
  };

  funcAddChildUk = id => {
    this.props.funcAddChildUk(id);
    this.handleClose();
  };

  funcSelected = node => {
    this.props.funcSelected(node);
  };

  funcRemoveUk = id => {
    this.props.funcRemoveUk(id);
    this.handleClose();
  };

  handleRightClick = event => {
    event.preventDefault();
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  renderTree = node => {
    return (
      <Box key={node.id}>
        <TreeItem
          nodeId={node.id}
          label={node.name}
          onClick={() => this.funcSelected(node)}
          onContextMenu={event => this.handleRightClick(event)}
          // 토글 되는 것을 방지
          // onIconClick={evnet => console.log(node.id)}
          onLabelClick={event => event.preventDefault()}
        >
          {Array.isArray(node.children)
            ? node.children.map(node => this.renderTree(node))
            : null}
        </TreeItem>
        {/* <Menu
          id={node.id}
          anchorEl={this.state.anchorEl}
          open={open}
          onClose={this.handleClose}
        >
          <MenuItem onClick={this.funcAddChildUk}>
            <ListItemIcon>
              <AddIcon />
            </ListItemIcon>
            <ListItemText primary="UK 추가하기" />
          </MenuItem>
          <MenuItem onClick={this.funcRemoveUk}>
            <ListItemIcon>
              <RemoveIcon />
            </ListItemIcon>
            <ListItemText primary="UK 제거하기" />
          </MenuItem>
        </Menu> */}
      </Box>
    );
  };

  TreeToArray = node => {
    let array = [];
    let recursivefunc = node => {
      array.push(node.id);
      if (Array.isArray(node.children)) {
        node.children.map(node => recursivefunc(node));
      }
    };
    recursivefunc(node);
    return array;
  };

  render() {
    const { data, target } = this.props;
    const open = Boolean(this.state.anchorEl);
    return (
      <TreeView
        defaultCollapseIcon={<ExpandMoreIcon />}
        defaultExpandIcon={<ChevronRightIcon />}
        selected={`${target.id}`}
        onNodeSelect={(evnet, value) => {
          return (
            <Menu
              id={value}
              anchorEl={this.state.anchorEl}
              open={open}
              onClose={this.handleClose}
            >
              <MenuItem onClick={() => this.funcAddChildUk(value)}>
                <ListItemIcon>
                  <AddIcon />
                </ListItemIcon>
                <ListItemText primary="UK 추가하기" />
              </MenuItem>
              <MenuItem onClick={() => this.funcRemoveUk(value)}>
                <ListItemIcon>
                  <RemoveIcon />
                </ListItemIcon>
                <ListItemText primary="UK 제거하기" />
              </MenuItem>
            </Menu>
          );
        }}
        onNodeToggle={(event, nodeIds) => console.log(nodeIds)}
      >
        {this.renderTree(data)}
      </TreeView>
    );
  }
}

export default ExList;
