import { observable, makeObservable, action, computed, toJS } from 'mobx';
import generateId from '../../IDGenerator';

class ExStore {
  constructor() {
    makeObservable(this);
  }

  @observable
  _target = {
    parentId: null,
    id: null,
    name: '',
    children: [],
  };

  @computed
  get target() {
    return toJS(this._target);
  }

  @action
  funcSelected(target) {
    this._target = target;
  }

  @observable
  _dupData = [];

  @computed
  get dupData() {
    return toJS(this._dupData);
  }

  @observable
  _beforeData = [
    {
      parentId: 'root',
      id: 'root',
    },
    {
      parentId: 'root',
      id: '1',
      name: '1',
    },
    {
      parentId: '1',
      id: '2',
      name: '2',
    },
    {
      parentId: '1',
      id: '3',
      name: '3',
    },
    {
      parentId: '1',
      id: '4',
      name: '4',
    },
    {
      parentId: '2',
      id: '5',
      name: '5',
    },
    {
      parentId: '2',
      id: '6',
      name: '6',
    },
    {
      parentId: '1',
      id: '10',
      name: '10',
    },
    {
      parentId: 'root',
      id: '21',
      name: '21',
    },
    {
      parentId: '21',
      id: '22',
      name: '22',
    },
  ];

  @computed
  get beforeData() {
    return toJS(this._beforeData);
  }

  @action
  funcTreeModel(arrayList, rootId) {
    let rootNodes = [];

    let traverse = function (nodes, item, index) {
      if (nodes instanceof Array) {
        return nodes.some(function (node) {
          if (node.id === item.parentId) {
            node.children = node.children || [];
            return node.children.push(arrayList.splice(index, 1)[0]);
          }
          return traverse(node.children, item, index);
        });
      }
    };

    while (arrayList.length > 0) {
      arrayList.some(function (item, index) {
        if (item.parentId === rootId) {
          return rootNodes.push(arrayList.splice(index, 1)[0]);
        }
        return traverse(rootNodes, item, index);
      });
    }
    return rootNodes;
  }

  @action
  funcGet() {
    console.log(this.beforeData);

    this._dupData = [...this._beforeData];
    console.log(this.beforeData);
    // this._data = this.funcTreeModel(this._dupData, 'root').filter(
    //   i => i.id !== 'root',
    // );
    console.log(this.dupData);
    console.log(this.beforeData);
    console.log(this.data);
  }

  @observable
  _data = [];

  @computed
  get data() {
    return toJS(this._data);
  }

  @action
  funcAddRootUk() {
    let tmp = generateId(5);
    let object = {
      parentId: 'root',
      id: tmp,
      name: tmp,
    };
    this._beforeData.push(object);
    this.funcGet();
  }

  @action
  funcAddChildUk() {
    this.funcChangeTarget();
  }

  @action
  funcChangeTarget() {
    let tmp = generateId(5);
    let child = {
      id: tmp,
      name: tmp,
    };

    const haveChildren = 'children' in this._target;

    if (haveChildren === true) {
      this._target = {
        ...this._target,
        ...this._target.children.push(child),
      };
    } else {
      this._target = {
        ...this._target,
        children: [child],
      };
    }
    console.log(toJS(this._target));

    let ff = this._data.findIndex(i => i.id === this._target.id);

    this._data.splice(ff, 1, this._target);
  }
}

export default new ExStore();
