import { observable, action, computed, makeObservable, toJS } from 'mobx';
import { throws } from 'node:assert';
import EkdbRepository from '../repository/EkdbRepository';

class EkdbStore {
  // 데코레이터기법을 사용하기 위해서
  // makeObservable 선언
  constructor() {
    makeObservable(this);
  }

  // 관찰 되어질
  // ekdb 목록 배열
  @observable
  _ekdbs = [];

  // 현재입력되는 정보
  @observable
  _target = {
    name: '',
    des: '',
  };

  // observable이 적용된 변수들은
  // 몹엑스 스토어가 관리하는 데이터,
  // 객체 형태로 랩핑이 된다.
  // 실제 자바스크립트 객체로 변경을 원할시에
  //
  // get ekdbs() {
  //   return this._ekdbs ? this.ekdbs.slice() : [];
  // }
  // [Proxy], [Proxy], [Proxy] ...
  //

  //toJS사용이유
  @computed
  get ekdbs() {
    return toJS(this._ekdbs);
  }

  @computed
  get target() {
    return toJS(this._target);
  }

  @action
  clear() {
    this._target = {
      name: '',
      des: '',
    };
  }

  @action
  funcGet() {
    EkdbRepository.funcGet()
      .then(
        action(response => {
          console.log(response);
          console.log(JSON.stringify(response));
          this._ekdbs = toJS(response);
          console.log(this._ekdbs);
          return this._ekdbs;
        }),
      )
      .catch(e => {
        alert(e);
      });
  }

  // 현재 입력되어있는 정보 (_target)을
  // Repository의 Restful Api를 이용하여 서버에 전송
  // id를 줘야한다면 여기서 줘야될듯
  @action
  funcInsert() {
    const data = {
      EKDB_NAME: this._target.name,
      EKDB_DES: this._target.des,
    };
    EkdbRepository.funcInsert(data)
      .then(
        action(response => {
          console.log(JSON.stringify(response));
          alert('insert success!');
          this.funcGet();
          this.clear();
        }),
      )
      .catch(e => alert(e));
  }

  @action
  funcUpdate() {
    const data = {
      EKDB_DES: this._target.des,
    };

    EkdbRepository.funcUpdate(this._target.name, data)
      .then(
        action(response => {
          console.log(JSON.stringify(response));
          alert('update success!');
          this.funcGet();
          this.clear();
        }),
      )
      .catch(e => {
        alert(e);
      });
  }

  @action
  funcDel() {
    EkdbRepository.funcDel(this._target.name)
      .then(
        action(response => {
          console.log(JSON.stringify(response));
          alert('delete success!');
          this.funcGet();
        }),
      )
      .catch(e => {
        alert(e);
      });
  }

  // 이벤트가 발생할 때 마다
  // _target의 정보를 변경
  // 기존에 가지고 있는 _target 값을 복사한 뒤에
  // 입력된 값을 변경
  @action
  funcOnChange(key, value) {
    this._target = {
      ...this._target,
      [key]: value,
    };
  }
}

export default new EkdbStore();
