import React from 'react';
import TreeView from '@material-ui/lab/TreeView';
import TreeItem from '@material-ui/lab/TreeItem';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import {
  Box,
  ListItemIcon,
  ListItemText,
  Menu,
  MenuItem,
} from '@material-ui/core';
import ExMoreHoriz from './ExMoreHoriz';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import { observer } from 'mobx-react';

@observer
class ExList extends React.Component {
  state = {
    anchorEl: null,
  };

  funcAddChildUk = id => {
    this.props.funcAddChildUk(id);
    this.handleClose();
  };

  funcSelected = (event, node) => {
    event.preventDefault();
    this.props.funcSelected(node);
  };

  funcRemoveUk = id => {
    this.props.funcRemoveUk(id);
    this.handleClose();
  };

  handleRightClick = event => {
    event.preventDefault();
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  renderTree = node => {
    const open = Boolean(this.state.anchorEl);
    return (
      <Box key={node.id}>
        <TreeItem
          nodeId={node.id}
          label={node.name}
          onClick={event => this.funcSelected(event, node)}
          onContextMenu={this.handleRightClick}
          // 토글 되는 것을 방지
          onLabelClick={event => event.preventDefault()}
        >
          {Array.isArray(node.children)
            ? node.children.map(node => this.renderTree(node))
            : null}
        </TreeItem>
        <Menu
          id={node.id}
          anchorEl={this.state.anchorEl}
          keepMounted
          open={open}
          onClose={this.handleClose}
        >
          <MenuItem onClick={() => this.funcAddChildUk(node.id)}>
            <ListItemIcon>
              <AddIcon />
            </ListItemIcon>
            <ListItemText primary="UK 추가하기" />
          </MenuItem>
          <MenuItem onClick={() => this.funcRemoveUk(node.id)}>
            <ListItemIcon>
              <RemoveIcon />
            </ListItemIcon>
            <ListItemText primary="UK 제거하기" />
          </MenuItem>
        </Menu>
      </Box>
    );
  };

  render() {
    const { data, target } = this.props;
    return (
      <TreeView
        defaultCollapseIcon={<ExpandMoreIcon />}
        defaultExpandIcon={<ChevronRightIcon />}
        selected={`${target.id}`}
      >
        {this.renderTree(data)}
      </TreeView>
    );
  }
}

export default ExList;
