import { observable, action, makeObservable, toJS, computed } from 'mobx';

class UkStore {
  constructor() {
    makeObservable(this);
  }

  // 서버에서 받아온 uk 리스트를 담을 배열
  @observable
  _uks = [];

  // 현재 입력 되고 있는 정보를 담을 오브젝트
  @observable
  _target = {
    id: null,
    name: '',
    des: '',
    questions: [],
  };

  // 현재 입력 되고 있는 질문
  @observable
  _question = '';

  @computed
  get uks() {
    return toJS(this._uks);
  }

  @computed
  get target() {
    return toJS(this._target);
  }

  @computed
  get question() {
    return this._question;
  }

  @action
  clear() {
    this._target = {
      id: null,
      name: '',
      des: '',
    };
  }

  //  **** UK 관련 함수 ****

  @action
  funcTargetOnChange(key, value) {
    this._target = {
      ...this._target,
      [key]: value,
    };
  }

  @action
  funcSelectedUk(target) {
    this._target = target;
  }

  @action
  funcAddUk(target) {
    this._uks.push(target);
    this.funcSelectedUk(target);
  }

  @action
  funcUpdateUk() {
    let foundUk = this._uks.find(uk => uk.id === this._target.id); // find 주어진 조건을 만족하는 첫번째 요소 값을 반환 else undefined
    foundUk.name = this._target.name;
    foundUk.des = this._target.des;
    foundUk.questions = this.target.questions;
  }

  @action
  funcRemoveUk() {
    let index = this._uks.findIndex(uk => uk.id === this._target.id);
    if (index > -1) {
      this._uks.splice(index, 1);
    }
    this.clear();
  }

  // **** Question 관련 함수 ****
  @action
  funcAddQuestion(question) {
    this.target.questions.push(question);
    this._question = '';
  }

  @action
  funcRemoveQuestion(id) {
    let index = this.target.questions.findIndex(question => question.id === id);
    if (index > -1) {
      this.target.questions.splice(index, 1);
    }
  }
  @action
  funcQuestionChange(question) {
    this._question = question;
  }

  @action
  funcKeyPress(event, question) {
    if (event.key === 'Enter') {
      this.funcAddQuestion(question);
    }
  }
}

export default new UkStore();
