import React, { PureComponent } from 'react';
import SaveIcon from '@material-ui/icons/Save'
import DeleteIcon from '@material-ui/icons/Delete';
import UpdateIcon from '@material-ui/icons/Update';
import DateFnsUtils from '@date-io/date-fns';
import { MuiPickersUtilsProvider , KeyboardDatePicker } from '@material-ui/pickers';
import { Grid, TextField, Button } from '@material-ui/core';

class TodoEditFormView extends PureComponent {
    render() {

        const { todo, onSetTodoProps } = this.props;

        return (
            <form noValidate>
                <Grid container spacing={3}>
                    <Grid item xs={3}>
                        <TextField
                            margin="normal"
                            id="outlined-basic"
                            labe="Title"
                            variant="standard"
                            value={todo && todo.title ? todo.title : ''}
                            onChange= {(event)=>onSetTodoProps('title', event.target.value)}
                        />
                    </Grid>
                    <Grid item xs={3}>
                        <MuiPickersUtilsProvider utils={DateFnsUtils}>
                            <KeyboardDatePicker
                                margin="normal"
                                id="date-picker-dialog"
                                label="Date"
                                format="yyyy-MM-dd"
                                value={todo && todo.date ? todo.date : null}
                                onChange={(date)=> onSetTodoProps('date', date.valueOf())}
                                KeyboardButtonProps={{
                                    'aria-label': 'change date',
                                }}
                                />
                        </MuiPickersUtilsProvider>
                    </Grid>
                </Grid>
                <Grid item>
                    <Button variant='contained' color='primary' startIcon={<SaveIcon />}>ADD</Button>&nbsp;&nbsp;
                    <Button variant='contained' color='primary' startIcon={<UpdateIcon/>}>ADD</Button>&nbsp;&nbsp;
                    <Button variant='contained' color='primary' startIcon={<DeleteIcon/>}>ADD</Button>&nbsp;&nbsp;
                </Grid>
            </form>
        );
    }
}

export default TodoEditFormView;
