import React from 'react';
import TreeView from '@material-ui/lab/TreeView';
import TreeItem from '@material-ui/lab/TreeItem';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import {
  Box,
  ListItemIcon,
  ListItemText,
  Menu,
  MenuItem,
} from '@material-ui/core';
import ExMoreHoriz from './ExMoreHoriz';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import { observer } from 'mobx-react';

@observer
class ExList extends React.Component {
  state = {
    anchorEl: null,
  };

  funcAddChildUk = id => {
    this.props.funcAddChildUk(id);
  };

  funcSelected = (event, node) => {
    event.preventDefault();
    this.props.funcSelected(node);
  };

  funcRemoveUk = () => {
    this.props.funcRemoveUk();
  };

  handleRightClick = event => {
    event.preventDefault();
    console.log('h1');
    return <ExMoreHoriz />;
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  renderTree = node => {
    const open = Boolean(this.state.anchorEl);
    return (
      <>
        <TreeItem
          key={node.id}
          nodeId={node.id}
          label={
            <Box key={node.id} sx={{ display: 'flex', alignItems: 'center' }}>
              <Box sx={{ flexGrow: 3 }}>{`${node.name}`}</Box>
              <Box>
                <ExMoreHoriz
                  nodeId={node.id}
                  funcAddChildUk={this.funcAddChildUk}
                  funcRemoveUk={this.funcRemoveUk}
                />
              </Box>
            </Box>
          }
          onClick={event => this.funcSelected(event, node)}
          onContextMenu={this.handleRightClick}
          // 토글 되는 것을 방지
          onLabelClick={event => event.preventDefault()}
        >
          {Array.isArray(node.children)
            ? node.children.map(node => this.renderTree(node))
            : null}
        </TreeItem>
        <Menu
          id="simple-menu"
          anchorEl={this.state.anchorEl}
          keepMounted
          open={open}
          onClose={this.handleClose}
        >
          <MenuItem onClick={this.funcAddChildUk}>
            <ListItemIcon>
              <AddIcon />
            </ListItemIcon>
            <ListItemText primary="UK 추가하기" />
          </MenuItem>
          <MenuItem onClick={this.funcRemoveUk}>
            <ListItemIcon>
              <RemoveIcon />
            </ListItemIcon>
            <ListItemText primary="UK 제거하기" />
          </MenuItem>
        </Menu>
      </>
    );
  };

  render() {
    const { data, target } = this.props;
    return (
      <TreeView
        defaultCollapseIcon={<ExpandMoreIcon />}
        defaultExpandIcon={<ChevronRightIcon />}
        selected={`${target.id}`}
      >
        {this.renderTree(data)}
      </TreeView>
    );
  }
}

export default ExList;
