import { observable, action, computed, makeObservable } from 'mobx'

class TodoStore {

    constructor() {
        makeObservable(this);
    }

    @observable
    _todo = {
    } // id, title, date

    get todo() {
        return this._todo;
    }

    @action
    setTodoProps(name, value) {
        this._todo = {
            ...this._todo, // ... 전개연산자
            [name] : value
        }
    }

}

// 일반 자바스크립트 class이기 때문에 객체생성을 해서 export
export default new TodoStore();
