import { observable, action, makeObservable } from 'mobx';

class CounterStore {

    constructor() {
        makeObservable(this);
    }


    @observable
    _count = 5

    // get Method
    // 변수에 접근 하듯이 사용하면 된다.
    get count() {
        return this._count;
    }

    @action
    increment() {
        this._count++;
    }

    @action
    decrement() {
        this._count--;
    }

}

export default new CounterStore();
