import React from 'react';
import TreeView from '@material-ui/lab/TreeView';
import TreeItem from '@material-ui/lab/TreeItem';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import { Box } from '@material-ui/core';
import ExMoreHoriz from './ExMoreHoriz';
import { observer } from 'mobx-react';

@observer
class ExList extends React.Component {
  renderTree = node => {
    const { funcSelected, funcAddChildUk, funcRemoveUk, target, _target } =
      this.props;
    return (
      <TreeItem
        key={node.id}
        nodeId={node.id}
        label={
          <Box key={node.id} sx={{ display: 'flex', alignItems: 'center' }}>
            <Box sx={{ flexGrow: 3 }}>{`${node.name}`}</Box>
            <Box>
              <ExMoreHoriz
                nodeId={node.id}
                funcAddChildUk={funcAddChildUk}
                funcRemoveUk={funcRemoveUk}
                target={target}
                _target={_target}
              />
            </Box>
            {console.log('ExMoreHoriz 밖')}
            {console.log(target.id)}
          </Box>
        }
        onClick={() => funcSelected(node)}
        //  onLabelClick을 막음으로 해당 UK클릭시 계속해서
        // 토글 되는 것을 방지
        onLabelClick={event => event.preventDefault()}
      >
        {console.log(target.id)}
        {Array.isArray(node.children)
          ? node.children.map(node => this.renderTree(node))
          : null}
      </TreeItem>
    );
  };

  render() {
    const { data, target, _selectedId } = this.props;
    console.log('이 아래로 뷰');
    console.log(target.id);
    return (
      <TreeView
        defaultCollapseIcon={<ExpandMoreIcon />}
        defaultExpandIcon={<ChevronRightIcon />}
        selected={target.id === null ? `${_selectedId}` : `${target.id}`}
      >
        {this.renderTree(data)}
      </TreeView>
    );
  }
}

export default ExList;
