import React from 'react';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import { IconButton, Menu, MenuItem } from '@material-ui/core';

class ExMoreHoriz extends React.Component {
  state = {
    anochorEl: null,
  };

  handleClick = event => {
    console.log(event.currentTarget);
    this.setState({ anochorEl: event.currentTarget });
    console.log(this.state.anochorEl);
  };

  handleClose = () => {
    this.setState({ anochorEl: null });
  };

  render() {
    return (
      <>
        <IconButton
          aria-controls="simple-menu"
          aria-haspopup="true"
          onClick={this.handleClick}
        >
          <MoreHorizIcon fontSize="small" />
        </IconButton>
        <Menu
          id="simple-menu"
          anchorEl={this.state.anchorEl}
          keepMounted
          open={Boolean(this.state.anchorEl)}
          onClose={this.handleClose}
        >
          <MenuItem onClick={this.handleClose}>+</MenuItem>
          <MenuItem onClick={this.handleClose}>-</MenuItem>
        </Menu>
      </>
    );
  }
}

export default ExMoreHoriz;
